import React from 'react';
import { BoardList } from '../components';

class Board extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      memberList: []
    };
  }

  // componentDidMount() {
  //   this._getList();
  // }

  // _getList() {
  //   const apiUrl = '../store/modules/member.js';

  //   axios.get(apiUrl).then(data => {
  //     this.setState({
  //       member : data.data.member
  //     });
  //   })
  //   .catch(error => {
  //     console.log(error);
  //   })
  // }

  componentDidMount() {
    fetch('http://localhost:3000/store/modules/member')
      .then(res => {
        return res.json();
      })
      .then(data => {
         this.setState({ 
           memberList: data.data.memberList 
         });
        })
  }

  render() {
    const { member } = this.props;

    return(
      <div>
        <BoardList member={member} />
      </div>
    );
  }
}

export default Board;
