import React from 'react';
import { connect } from 'react-redux';

import { board_save } from '../reducers/board'

class BoardForm extends Component {
  state = {};
  initialSelectedBoard = {
      num: "",
      name: "",
      rank: "",
      department: ""
  };

  handleChange = (e) => {
      this.setState ({[e.target.name]: e.target.value});
  }
  
  handleSave = () => {
      this.props.dispatch(board_save(this.state));
      this.setState (this.initialSelectedBoard);
  }
  
  componentWillReceiveProps(nextProps) {
      this.setState (nextProps.selectedBoard);
  }
  
  render() {
      return (
          <div>
              <input placeholder="title" name="name" value={this.state.name} onChange={this.handleChange} />
              <input placeholder="name" name="rank" value={this.state.rank} onChange={this.handleChange} />
              <button onClick={this.handleSave}>Save</button>
          </div>
      );
  }
}

let mapStateToProps = (state) => {
  return {
      selectedBoard: state.selectedBoard
  };
}
export default connect(mapStateToProps)(BoardForm);
