import React from 'react';
import { connect } from 'react-redux';
import { Header, BoardList } from 'components';
import { boardPostRequest } from '../actions/board';
import 'App.css';

class Home extends React.Component {
    render(){
        let re = /(login|detail)/;
        let isAuth = re.test(this.props.location.pathname);

        const board = (
            <BoardList/>
        );

        return (
            <div>
               {isAuth ? undefined : <Header />}
               { this.props.isLoggedIn ? board : undefined }
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        isLoggedIn: state.authentication.status.isLoggedIn,
        postStatus: state.board.post
    };
};

const mapDispatchToProps = (dispatch) => {
    return {
        boardPostRequest: (contents) => {
            return dispatch(boardPostRequest(contents));
        }
    };
};


export default connect(mapStateToProps, mapDispatchToProps) (Home);
