import React from 'react';
import 'App.css';
import axios from 'axios';
import { BoardList } from '../components';

class Board extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      member: []
    };
  }

  componentDidMount() {
    this._getList();
  }

  _getList() {
    const apiUrl = '../store/modules/member.js';

    axios.get(apiUrl).then(data => {
      this.setState({
        member : data.data.member
      });
    })
    .catch(error => {
      console.log(error);
    })
  }

  render() {
    return(
      <div>
        <BoardList member={this.state.member} />
      </div>
    );
  }
}

