import axios from 'axios';

import {
    BOARD_POST,
    BOARD_POST_SUCCESS,
    BOARD_POST_FAILURE,
    MEMO_LIST,
    MEMO_LIST_SUCCESS,
    MEMO_LIST_FAILURE
} from './ActionTypes';

export function boardPostRequest(contents) {
    return (dispatch) => {
        dispatch(boardPost());

        return axios.post('/board', { contents })
        .then((response) => {
            dispatch(boardPostSuccess());
        }).catch((error) => {
            dispatch(boardPostFailure(error.response.data.code));
        });
    };
}

export function boardPost() {
    return {
        type: BOARD_POST
    };
}

export function boardPostSuccess() {
    return {
        type: BOARD_POST_SUCCESS
    };
}

export function boardPostFailure(error) {
    return {
        type: BOARD_POST_FAILURE,
        error
    };
}
