import React from 'react';
import axios from 'axios';
import { BoardList } from '../components';

class Board extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      member: []
    };
  }

  // componentDidMount() {
  //   this._getList();
  // }

  // _getList() {
  //   const apiUrl = '../store/modules/member.js';

  //   axios.get(apiUrl).then(data => {
  //     this.setState({
  //       member : data.data.member
  //     });
  //   })
  //   .catch(error => {
  //     console.log(error);
  //   })
  // }

  componentDidMount() {
    fetch('http://localhost:3000/store/modules/member')
      .then(res => res.json())
      .then(data => this.setState({member : data.data.member}));
  }

  render() {
    return(
      <div>
        <BoardList member={this.state.member} />
      </div>
    );
  }
}

function createData(id, name, rank, department) {
  return { id, name, rank, department };
}

export default Board;
