import React, { Component } from 'react';
import { connect } from 'react-redux';

import { board_save } from '../reducers/board'

class BoardForm extends Component {
    constructor(props) {
        super(props);
        this.state = {};

        this.handleChange = this.handleChange.bind(this);
        this.handleSave = this.handleSave.bind(this);
    }

    initialSelectedBoard = {
        brdno: "",
        brdtitle: "",
        brdwriter: "",
        brddate: ""
    };

    handleChange(event) {
        this.setState ({value: event.target.value});
    }
    
    handleSave = () => {
        this.props.dispatch(board_save(this.state));
        this.setState (this.initialSelectedBoard);
    }
    
    componentWillReceiveProps(nextProps) {
        this.setState (nextProps.selectedBoard);
    }
    
    render() {
        return (
            <div>
                <input placeholder="title" name="brdtitle" value={this.state.value} onChange={this.handleChange} />
                <input placeholder="name" name="brdwriter" value={this.state.value} onChange={this.handleChange} />
                <button onClick={this.handleSave}>Save</button>
            </div>
        );
    }
}

let mapStateToProps = (state) => {
    return {
        selectedBoard: state.selectedBoard
    };
}
export default connect(mapStateToProps)(BoardForm);
