import React from 'react';
import { connect } from 'react-redux';

import { board_read, board_remove } from '../reducers/board'

class BoardItem extends Component {
    handleUpdateForm = (num) => {
        this.props.dispatch(board_read(num));
    }    
    
    render() {
        const row= this.props.row
        
        return(
            <tr>
                <td>{row.num}</td>
                <td><a onClick={() => this.handleUpdateForm(row.num) }>{row.name}</a></td>
                <td>{row.rank}</td>
                <td>{row.department}</td>
                <td><a onClick={() => { this.props.dispatch(board_remove(row.num)) }}>X</a></td>
            </tr>
        );
    }
}

export default connect()(BoardItem)
