import React from 'react';
import 'App.css';
import { Link } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Fab from '@material-ui/core/Fab';
import AddIcon from '@material-ui/icons/Add';
import Button from '@material-ui/core/Button';
// import DeleteIcon from '@material-ui/icons/Delete';

const useStyles = makeStyles(theme => ({
  root: {
    width: '70%',
    margin: '2.5rem auto',
    overflowX: 'auto',
    border: '1px solid #eee',
    borderRadius: '5px'
  },
  table: {
    minWidth: 650
  },
  tbody: {
    cursor: 'pointer'
  }
}));

function createData(name, rank, department) {
  return { name, rank, department, };
}

const rows = [
  createData('Jake, JunKil Been', 'CEO', ''),
  createData('DongHyeon Kim', 'CTO', '', ''),
  createData('DaeGyu Min', 'Leader', 'Development', ''),
  createData('KyeHyun Yoon', 'Manager', 'Management strategy'),
  createData('DongJun Jung', 'Researcher', 'Algorithm of analysis software'),
  createData('JeongHwan Bae', 'SW Engineer', 'Development'),
  createData('YiJin Kim', 'HR Manager', 'Management strategy'),
  createData('DSangJin Han', 'UX/UI Designer', 'Development'),
  createData('DaYeon Kim', 'Financial Manager', 'Management strategy'),
  createData('​JungAh Lee', 'QA Engineer', 'Development'),
  createData('KiCher Park', 'SW Engineer', 'Algorithm of analysis software'),
  createData('BoReum Yoo', 'RA Specialist', 'Development'),
  createData('SeonJik Kim', 'Researcher', 'Algorithm of analysis software'),
  createData('​TaeYeong Kim', 'Senior Researcher, P.T.', 'Algorithm of analysis software'),
  createData('SeongJin Son', 'Senior Researcher', 'Algorithm of analysis software'),
  createData('MinYoung Jung', 'Leader', 'Development'),
  createData('​WonJae Chang', 'Global Sales & Business Manager', 'Management strategy'),
  createData('SinYu Jeong', 'Researcher', 'Algorithm of analysis software'),
  createData('JungHoon Park', 'SW Engineer', 'Development'),
  createData('​DaHye Kim', 'Marketing Manager', 'Management strategy'),
  createData('SeungTaek No', 'Manager', 'Management strategy'),
  createData('HanSol Kim', 'SW Engineer', 'Development'),
];

export default function SimpleTable() {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <Fab align="right" aria-label="add" className={classes.fab}>
        <AddIcon />
      </Fab>
      <Table className={classes.table}>
        <TableHead>
          <TableRow>
            <TableCell>Name</TableCell>
            <TableCell align="right">Rank</TableCell>
            <TableCell align="right">Department</TableCell>
            <TableCell align="right"></TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {rows.map(row => (
            <Link to="/detail"><TableRow key={row.name} hover role="checkbox">
              <TableCell component="th" scope="row">
                {row.name}
              </TableCell>
              <TableCell align="right">{row.rank}</TableCell>
              <TableCell align="right">{row.department}</TableCell>
              <TableCell align="right">
                <Button color="primary" size="small" className={classes.button}>수정</Button>
                <Button color="secondary" size="small" className={classes.button}>삭제</Button>
              </TableCell>
            </TableRow>
            </Link>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}
